import { useState } from "react";
import axios from 'axios';

function App() {

  const [title, setTitle] = useState('');
  const [body, setBody] = useState('');

  const onSubmit = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:3009/posts', {
        title: title, 
        body: body  // 오타 수정
      });
      console.log('Post Created:', response.data);
      // 성공 후 추가 동작 (예: 폼 초기화)
    } catch (error) {
      console.error('Error posting data:', error);
      // 실패 처리 로직
    }
  };

  return (
    <div className="container">
      <div className="mb-3">
        <label className="form-label">Title</label>  {/* 오타 수정 */}
        <input 
          className="form-control" 
          value={title}
          onChange={(event) => setTitle(event.target.value)}
        />
      </div>
      <div className="mb-3">
        <label className="form-label">Body</label>  {/* 오타 수정 */}
        <textarea 
          className="form-control" 
          value={body}
          onChange={(event) => setBody(event.target.value)}
          rows={20}
        />
      </div>
      <button 
        className="btn btn-primary"
        onClick={onSubmit}
      >
        Post
      </button>
    </div>
  );
}

export default App;
