    console.log(id)

    const getPost = (id) => {
        axios.get(`http://localhost:3009/posts/${id}`).then((res) => {
            setPost(res.data)
        })
    };

    useEffect (() => {
        getPost(id)
    }, [])

    return (
        <div>showpage</div>
